import React, { Component } from "react";
import TableRowComponent from './TableRowComponent';

export default class TableComponent extends Component { 
    render() { 
        const courses = [
            {id : 1,    courseName: 'First',            prefix: 'FIR',  languages: '1',   grade: 'G'},
            {id : 2,    courseName: 'Second',            prefix: 'SEC',  languages: '2',   grade: 'G'},
            {id : 3,    courseName: 'Third',            prefix: 'THR',  languages: '3',   grade: 'G'},
            {id : 4,    courseName: 'Fourth',            prefix: 'FOU',  languages: '4',   grade: 'G'},
        ]

        const rows = [];

        courses.forEach((course) => {
            rows.push(
                <TableRowComponent
                    course={course}
                    key={course.id}
                />
            );
        });

        return ( 
            <div>
                <div className="bg-primary text-white text-center p-2 m-4">
                    <h4>
                        Semesters and Courses
                    </h4>
                </div>
                <table className="w-auto mx-auto" style={{width: "200"}} bordered hover>
                    <thead>
                        <tr className="bg-white text-black text-left p-2 m-4 border-white fs-4 fw-bold border-bottom-0 border-3"> 
                            <th colSpan={2}>University Name </th> 
                            <th>Year</th> 
                            <th>Months</th> 
                        </tr>
                        <tr className="bg-white text-black text-left p-2 m-4 border-danger fs-5 fw-bold border border-3 text-center">
                            <th className="p-1 m-4 border-danger border border-3">Course Name</th>
                            <th className="border-danger border border-3">Course Prefix</th>
                            <th className="border-danger border border-3">Languages/Platforms</th>
                            <th className="border-danger border border-3">Grade</th>

                        </tr>
                     </thead>
                    <tbody className="bg-white text-black text-left p-2 m-4 border-danger fs-6 fw-bold border border-3">
                        {rows}
                    </tbody>
                </table>
            </div>
        );
    }
}
